/*! pdfslick.min.js | Versión mejorada y estéticamente agradable */
(function ($) {
    $.fn.pdfSlick = function (options) {
        // Opciones por defecto con estilos adicionales
        const settings = $.extend(
            {
                pdf: "",                 // URL del PDF
                scale: 1,                // Escala (no se utiliza en este ejemplo)
                width: "100%",           // Ancho del iframe
                height: "650px",         // Altura del iframe
                border: "none",          // Borde del iframe
                borderRadius: "4px",     // Bordes redondeados
                boxShadow: "0 2px 6px rgba(0,0,0,0.15)", // Sombra para un efecto moderno
                backgroundColor: "#fff"  // Color de fondo del contenedor
            },
            options
        );

        return this.each(function () {
            const $this = $(this);
            // Crear un iframe para mostrar el PDF con estilos adicionales
            const iframe = $("<iframe>", {
                src: settings.pdf,
                css: {
                    width: settings.width,
                    height: settings.height,
                    border: settings.border,
                    borderRadius: settings.borderRadius,
                    boxShadow: settings.boxShadow
                }
            });
            // Establecer el color de fondo del contenedor
            $this.css("background-color", settings.backgroundColor);
            // Limpiar el contenedor y agregar el iframe
            $this.empty().append(iframe);
        });
    };
})(jQuery);